	//参数,中文字符串  
	//返回值:拼音首字母串数组  
	function makePy(str) {
		if(typeof(str) != "string")
			throw new Error(-1, "函数makePy需要字符串类型参数!");
		var arrResult = new Array(); //保存中间结果的数组  
		for(var i = 0, len = str.length; i < len; i++) {
			//获得unicode码  
			var ch = str.charAt(i);
			//检查该unicode码是否在处理范围之内,在则返回该码对映汉字的拼音首字母,不在则调用其它函数处理  
			arrResult.push(checkCh(ch));
		}
		//处理arrResult,返回所有可能的拼音首字母串数组  
		return mkRslt(arrResult);
	}

	function checkCh(ch) {
		var uni = ch.charCodeAt(0);
		//如果不在汉字处理范围之内,返回原字符,也可以调用自己的处理函数  
		if(uni > 40869 || uni < 19968)
			return ch; //dealWithOthers(ch);  
		//检查是否是多音字,是按多音字处理,不是就直接在strChineseFirstPY字符串中找对应的首字母  
		return(oMultiDiff[uni] ? oMultiDiff[uni] : (strChineseFirstPY.charAt(uni - 19968)));
	}

	function mkRslt(arr) {
		var arrRslt = [""];
		for(var i = 0, len = arr.length; i < len; i++) {
			var str = arr[i];
			var strlen = str.length;
			if(strlen == 1) {
				for(var k = 0; k < arrRslt.length; k++) {
					arrRslt[k] += str;
				}
			} else {
				var tmpArr = arrRslt.slice(0);
				arrRslt = [];
				for(k = 0; k < strlen; k++) {
					//复制一个相同的arrRslt  
					var tmp = tmpArr.slice(0);
					//把当前字符str[k]添加到每个元素末尾  
					for(var j = 0; j < tmp.length; j++) {
						tmp[j] += str.charAt(k);
					}
					//把复制并修改后的数组连接到arrRslt上  
					arrRslt = arrRslt.concat(tmp);
				}
			}
		}
		return arrRslt;
	}
	
	$(function() {
		var Initials = $('.initials');
		var LetterBox = $('#letter');
		Initials.find('ul').append('<li>A</li><li>B</li><li>C</li><li>D</li><li>E</li><li>F</li><li>G</li><li>H</li><li>I</li><li>J</li><li>K</li><li>L</li><li>M</li><li>N</li><li>O</li><li>P</li><li>Q</li><li>R</li><li>S</li><li>T</li><li>U</li><li>V</li><li>W</li><li>X</li><li>Y</li><li>Z</li><li>#</li>');
		Initials.find('ul').find("li").css("list-style", "none");
		initials();

		$(".initials ul li").click(function() {
			var _this = $(this);
			var LetterHtml = _this.html();
			LetterBox.html(LetterHtml).fadeIn();

			Initials.css('background', 'rgba(145,145,145,0.6)');

			setTimeout(function() {
				Initials.css('background', 'rgba(145,145,145,0)');
				LetterBox.fadeOut();
			}, 1000);
			
			var _index = _this.index()
			if(_index == 0) {
				console.log($('#default').position().top);
				$('html,body').animate({
					scrollTop: '0px'
				}, 300); //点击第一个滚到顶部
			} else if(_index == 27) {
				var DefaultTop = $('#default').position().top;
				$('html,body').animate({
					scrollTop: DefaultTop + 'px'
				}, 300); //点击最后一个滚到#号
			} else {
				var letter = _this.text();
				if($('#' + letter).length > 0) {
					var LetterTop = $('#' + letter).position().top;
					$('html,body').animate({
						scrollTop: LetterTop - 45 + 'px'
					}, 300);
				}
			}
		})

		var windowHeight = $(window).height();
		var InitHeight = windowHeight - 45;
		Initials.height(InitHeight);
		var LiHeight = InitHeight / 28;
		Initials.find('li').height(LiHeight);
	})

	function initials() { //公众号排序
		var SortList = $(".sort_list");
		var SortBox = $(".sort_box");
		SortList.sort(asc_sort).appendTo('.sort_box'); //按首字母排序
		function asc_sort(a, b) {
			return makePy($(b).find('.num_name').text().charAt(0))[0].toUpperCase() < makePy($(a).find('.num_name').text().charAt(0))[0].toUpperCase() ? 1 : -1;
		}

		var initials = [];
		var num = 0;
		SortList.each(function(i) {
			var initial = makePy($(this).find('.num_name').text().charAt(0))[0].toUpperCase();
			if(initial >= 'A' && initial <= 'Z') {
				if(initials.indexOf(initial) === -1)
					initials.push(initial);
			} else {
				num++;
			}

		});

		$.each(initials, function(index, value) { //添加首字母标签
			SortBox.append('<div  style = "height:25px;line-height: 25px;padding-left: 20px;color: #787878;font-size: 14px;border-bottom: 1px solid #ddd;background-color: rgb(239, 239, 244);" class="sort_letter" id="' + value + '">' + value + '</div>');
		});
		if(num != 0) {
			SortBox.append('<div style = "height:25px;line-height: 25px;padding-left: 20px;color: #787878;font-size: 14px;background-color: rgb(239, 239, 244);" class="sort_letter" id="default">#</div>');
		}

		for(var i = 0; i < SortList.length; i++) { //插入到对应的首字母后面
			var letter = makePy(SortList.eq(i).find('.num_name').text().charAt(0))[0].toUpperCase();
			switch(letter) {
